CREATE TABLE  IF NOT EXISTS shows (show_id INT GENERATED ALWAYS AS IDENTITY, title VARCHAR(1000), url TEXT, description TEXT, imageUrl TEXT, created_on TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(show_id));
CREATE TABLE IF NOT EXISTS episodes (episode_id INT GENERATED ALWAYS AS IDENTITY, show_id INT, info_url TEXT, media_url TEXT, created_on TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(episode_id), CONSTRAINT fk_show FOREIGN KEY(show_id) REFERENCES shows(show_id));
